import './App.css';
import React , {Component} from 'react';



class App extends Component {
constructor(){
  super()
  this.handleMouseOver = this.handleMouseOver.bind(this)
  this.handleClick = this.handleClick.bind(this)
  this.handleKey = this.handleKey.bind(this)
}
state= {
  variable:'button',
  clicks:0,
  mouseover:0,
  keyUp:0
}

  handleMouseOver(){
   this.setState((before,_props)=>({

    mouseover: before.mouseover + 1,
    variable:this.state.mouseover,

   }))
   console.log(this)
  }
   handleClick(){
    this.setState((before,_props)=>({
      clicks: before.clicks + 1
     }))
  }
   handleKey(){
    this.setState((before,_props)=>({
      keyUp: before.keyUp + 1
     }))
  }
  render(){
    const {clicks,mouseover,keyUp ,variable} = this.state
    return (
      <div>
        <button onMouseOver={this.handleMouseOver}>{variable}</button>
        <button onClick={this.handleClick}>{variable}</button>
        <button onKeyUp={this.handleKey}>{variable}</button>
      </div>
    )
  }




}

export default App;
